INSERT INTO `wp_posts` VALUES(191, 1, '2014-03-27 14:01:08', '2014-03-27 14:01:08', '<div id="text1">\r\n<h1 class="act"><strong>Preferred Provider Organizations</strong> (PPO''s)</h1>\r\n&nbsp;\r\n\r\nPPO''s charge on a fee-for-service basis. The participating doctors, hospitals and health care providers are paid by the insurer on a negotiated, discounted fee schedule. Costs are lower if you use in-network healthcare services, but you have the option of going out-of-network. If you choose an out-of-network provider, you are generally required to pay the difference between what the provider charges and what the plan pays.\r\n\r\n</div>', 'Individual PPO', '', 'publish', 'open', 'open', '', 'individual-ppo', '', '', '2014-03-29 11:07:33', '2014-03-29 11:07:33', '', 185, 'http://jimenez.net16.net/?page_id=191', 1, 'page', '', 0);
