INSERT INTO `wp_posts` VALUES(195, 1, '2014-03-27 14:01:59', '2014-03-27 14:01:59', '<div id="text1">\r\n<h1 class="act">Short Term Insurance</h1>\r\n&nbsp;\r\n\r\nThis is a type of disability income coverage that provides benefits for a maximum benefit period of one to five years. Contrast with long-term disability income insurance.\r\n\r\n</div>', 'Short Term', '', 'publish', 'open', 'open', '', 'short-term', '', '', '2014-03-29 11:09:38', '2014-03-29 11:09:38', '', 185, 'http://jimenez.net16.net/?page_id=195', 3, 'page', '', 0);
