INSERT INTO `wp_posts` VALUES(199, 1, '2014-03-27 14:02:54', '2014-03-27 14:02:54', '<div id="text1">\r\n<h1 class="act"><strong>Vision Insurance</strong></h1>\r\n&nbsp;\r\n\r\nVision Insurance  is a form of <a title="Insurance" href="http://en.wikipedia.org/wiki/Insurance">insurance</a> that provides coverage for the services rendered by <a title="Eye care professional" href="http://en.wikipedia.org/wiki/Eye_care_professional">eye care professionals</a> such as <a title="Ophthalmology" href="http://en.wikipedia.org/wiki/Ophthalmology">ophthalmologists</a>and <a title="Optometry" href="http://en.wikipedia.org/wiki/Optometry">optometrists</a>. There are many vision insurance companies. The typical vision insurance plan provides yearly coverage for <a title="Eye examination" href="http://en.wikipedia.org/wiki/Eye_examination">eye examinations</a> and partial or full coverage for <a title="Glasses" href="http://en.wikipedia.org/wiki/Glasses">eyeglasses</a>, <a title="Sunglasses" href="http://en.wikipedia.org/wiki/Sunglasses">sunglasses</a>, and <a title="Contact lens" href="http://en.wikipedia.org/wiki/Contact_lens">contact lenses</a>, with or without <a title="Copay" href="http://en.wikipedia.org/wiki/Copay">copays</a>, depending on the plan chosen.\r\n\r\n</div>', 'Vision', '', 'publish', 'open', 'open', '', 'vision', '', '', '2014-03-29 11:11:37', '2014-03-29 11:11:37', '', 185, 'http://jimenez.net16.net/?page_id=199', 5, 'page', '', 0);
