INSERT INTO `wp_posts` VALUES(201, 1, '2014-03-27 14:03:19', '2014-03-27 14:03:19', '<div>\r\n<h1 class="act">Medical Equipment Supply Stores</h1>\r\n&nbsp;\r\n\r\nGet equipped for more success. This program is for medical equipment dealers, including rental and leasing services, whose primary business is the rental and sale of home healthcare products to consumers.\r\n\r\n&nbsp;\r\n\r\nGeneral Liability\r\nPrimary Limits up to $3,000,000 Occurrence/Aggregate\r\nHired and Non-owned Auto Included\r\nCoverages: Medical Payments Coverage - $5,000 Limit\r\nErrors and Omissions\r\nSexual and Physical Abuse\r\nAdditional Interests\r\nProperty Damage Extension (Care, Custody, and Control)- $500 Occurrence/ $1,000 Aggregate (Higher optional limits available.) No deductible required\r\n\r\n&nbsp;\r\nProperty\r\nBasic, Broad or Special Form\r\nReplacement Cost or ACV\r\nBuilding Contents\r\nBusiness Income\r\nEquipment Breakdown\r\nAccounts Receivable\r\nComputer Equipment\r\nOutside Signs\r\nValuable Papers\r\nCrime Inside the Premises\r\nTheft of Money and Securities Outside the Premises\r\n\r\n</div>', 'Medical Supplements', '', 'publish', 'open', 'open', '', 'medical-supplements', '', '', '2014-03-29 11:14:20', '2014-03-29 11:14:20', '', 185, 'http://jimenez.net16.net/?page_id=201', 6, 'page', '', 0);
